import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { setUserAuthStates } from "../../redux/slices/userAuthSlice";
import axios from "axios";
import "./Login.css";

const Login = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [credentials, setCredentials] = useState({
    mobile_number: "",
    pin: "",
  });

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("YOUR_API_ENDPOINT_HERE", credentials);

      if (res?.data?.jwt) {
        dispatch(setUserAuthStates({ key: "jwt", value: res.data.jwt }));
        dispatch(setUserAuthStates({ key: "refreshToken", value: res.data.refresh_token }));
        dispatch(setUserAuthStates({ key: "login", value: true }));

        navigate("/dashboard");
      } else {
        alert("Invalid credentials, please try again.");
      }
    } catch (error) {
      alert("Error logging in. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <h1>LocalSe</h1>
      <h2>Admin Login</h2>
      <form onSubmit={handleLogin}>
        <input type="text" name="mobile_number" placeholder="Username" value={credentials.mobile_number} onChange={handleChange} required />
        <input type="password" name="pin" placeholder="Password" value={credentials.pin} onChange={handleChange} required />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
