import React, { useState, useEffect } from 'react';

const AddServices = () => {
  const [serviceName, setServiceName] = useState('');
  const [category, setCategory] = useState('Home Services');
  const [keywords, setKeywords] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [services, setServices] = useState([
    { _id: '1', name: 'House Cleaning', category: 'Home Services', keywords: ['cleaning', 'housekeeping', 'maid'] },
    { _id: '2', name: 'Plumbing', category: 'Home Services', keywords: ['pipes', 'water', 'bathroom'] },
    { _id: '3', name: 'Hair Styling', category: 'Personal Services', keywords: ['haircut', 'salon', 'beauty'] },
    { _id: '4', name: 'Car Washing', category: 'Cleaning & Maintenance', keywords: ['auto', 'vehicle', 'cleaner'] },
    { _id: '5', name: 'Home Shifting', category: 'Shifting & Transport', keywords: ['moving', 'relocation', 'packers'] }
  ]);

  const categories = [
    "Home Services",
    "Personal Services",
    "Cleaning & Maintenance",
    "Shifting & Transport",
    "Event Services",
    "IT & Education",
    "Health & Wellness",
    "Government & Legal",
    "Construction & Labour",
    "Business & Office"
  ];

  const handleAddService = () => {
    if (!serviceName.trim()) {
      alert("Service name cannot be empty");
      return;
    }

    // Process keywords: split by commas and trim each keyword
    const keywordArray = keywords
      .split(',')
      .map(keyword => keyword.trim())
      .filter(keyword => keyword.length > 0);

    const newService = {
      _id: Date.now().toString(), // Generate a simple unique ID
      name: serviceName,
      category: category,
      keywords: keywordArray
    };

    setServices([...services, newService]);
    setServiceName('');
    setKeywords('');
    alert("Service added successfully");
  };

  const handleDelete = (id) => {
    const updatedServices = services.filter(service => service._id !== id);
    setServices(updatedServices);
    alert("Service deleted successfully");
  };

  // Filter services based on search query
  const filteredServices = services.filter(service => 
    service.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    service.category.toLowerCase().includes(searchQuery.toLowerCase()) ||
    service.keywords.some(keyword => 
      keyword.toLowerCase().includes(searchQuery.toLowerCase())
    )
  );

  const styles = {
    container: {
      padding: '20px',
      maxWidth: '800px',
      margin: '0 auto'
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '20px'
    },
    inputGroup: {
      display: 'flex',
      gap: '10px',
      marginBottom: '20px',
      alignItems: 'flex-end',
      flexWrap: 'wrap'
    },
    fieldContainer: {
      display: 'flex',
      flexDirection: 'column',
      flex: '1',
      minWidth: '200px'
    },
    fieldLabel: {
      marginBottom: '5px',
      fontWeight: 'bold',
      fontSize: '14px'
    },
    input: {
      padding: '8px',
      borderRadius: '4px',
      border: '1px solid #ccc',
      width: '100%'
    },
    select: {
      padding: '8px',
      borderRadius: '4px',
      border: '1px solid #ccc',
      width: '100%'
    },
    button: {
      padding: '8px 15px',
      backgroundColor: '#007BFF',
      color: 'white',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      height: '38px'
    },
    searchContainer: {
      marginBottom: '20px',
      display: 'flex',
      flexDirection: 'column'
    },
    listContainer: {
      maxHeight: 'calc(100vh - 300px)',
      overflowY: 'auto'
    },
    listItem: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '10px',
      borderBottom: '1px solid #eee',
      backgroundColor: '#f9f9f9',
      marginBottom: '8px',
      borderRadius: '4px'
    },
    serviceInfo: {
      flex: '1'
    },
    keywordsList: {
      display: 'flex',
      flexWrap: 'wrap',
      gap: '5px',
      marginTop: '5px'
    },
    keywordTag: {
      backgroundColor: '#e7f3ff',
      borderRadius: '4px',
      padding: '2px 6px',
      fontSize: '12px',
      color: '#0066cc'
    },
    deleteButton: {
      backgroundColor: '#dc3545',
      color: 'white',
      border: 'none',
      borderRadius: '4px',
      padding: '5px 10px',
      cursor: 'pointer'
    },
    helperText: {
      fontSize: '12px',
      color: '#6c757d',
      marginTop: '3px'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h2>Services</h2>
      </div>

      {/* Add Service Form */}
      <div style={styles.inputGroup}>
        <div style={styles.fieldContainer}>
          <label style={styles.fieldLabel}>Enter Service Name</label>
          <input
            type="text"
            placeholder="Service Name"
            value={serviceName}
            onChange={(e) => setServiceName(e.target.value)}
            style={styles.input}
          />
        </div>

        <div style={styles.fieldContainer}>
          <label style={styles.fieldLabel}>Choose Category</label>
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            style={styles.select}
          >
            {categories.map((cat, index) => (
              <option key={index} value={cat}>
                {cat}
              </option>
            ))}
          </select>
        </div>

        <div style={styles.fieldContainer}>
          <label style={styles.fieldLabel}>Synonyms (Keywords)</label>
          <input
            type="text"
            placeholder="Enter keywords separated by commas"
            value={keywords}
            onChange={(e) => setKeywords(e.target.value)}
            style={styles.input}
          />
          <div style={styles.helperText}>Example: cleaning, housekeeping, maid</div>
        </div>

        <button onClick={handleAddService} style={styles.button}>
          Add Service
        </button>
      </div>

      {/* Search */}
      <div style={styles.searchContainer}>
        <label style={styles.fieldLabel}>Search Services</label>
        <input
          type="text"
          placeholder="Search by name, category or keywords..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={styles.input}
        />
      </div>

      {/* Services List */}
      <div style={styles.listContainer}>
        {filteredServices.length > 0 ? (
          filteredServices.map((service) => (
            <div key={service._id} style={styles.listItem}>
              <div style={styles.serviceInfo}>
                <strong>{service.name}</strong>
                <div>Category: {service.category}</div>
                <div style={styles.keywordsList}>
                  {service.keywords && service.keywords.map((keyword, index) => (
                    <span key={index} style={styles.keywordTag}>{keyword}</span>
                  ))}
                </div>
              </div>
              <button
                style={styles.deleteButton}
                onClick={() => handleDelete(service._id)}
              >
                Delete
              </button>
            </div>
          ))
        ) : (
          <p>No services found</p>
        )}
      </div>
    </div>
  );
};

export default AddServices;
