import React, { useState } from "react";
import { useDispatch } from "react-redux";
import {
  logoutUser,
  setUserAuthStates,
} from "../../redux/slices/userAuthSlice";

const SideBar = ({ onSelect }) => {
  return (
    <div style={styles.sidebar}>
      <h1 style={styles.sidebarTitle}>LOCALSE</h1>
      <div style={styles.menu}>
        <div
          style={styles.menuItem}
          onClick={() => onSelect("notice")}
          onMouseOver={(e) => (e.target.style.background = "#ddd")}
          onMouseOut={(e) => (e.target.style.background = "white")}
        >
          Notice
        </div>
        <div
          style={styles.menuItem}
          onClick={() => onSelect("notification")}
          onMouseOver={(e) => (e.target.style.background = "#ddd")}
          onMouseOut={(e) => (e.target.style.background = "white")}
        >
          Notification
        </div>
      </div>
    </div>
  );
};

const Header = () => {
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logoutUser());
  };

  return (
    <div style={styles.header}>
      <span>Employee Name</span>
      <span
        style={styles.logout}
        onMouseOver={(e) => (e.target.style.color = "darkred")}
        onMouseOut={(e) => (e.target.style.color = "red")}
        onClick={handleLogout}
      >
        Logout
      </span>
    </div>
  );
};

// Notice Component
const NoticeList = () => {
  const [notices, setNotices] = useState([
    { id: 1, text: "Notice 1" },
    { id: 2, text: "Notice 2" },
  ]);

  const handleDelete = (id) => {
    setNotices(notices.filter((notice) => notice.id !== id));
  };

  return (
    <div style={styles.listContainer}>
      <h2>Notices</h2>
      {notices.map((notice) => (
        <div key={notice.id} style={styles.listItem}>
          {notice.text}
          <div>
            <button style={styles.editButton}>Edit</button>
            <button
              style={styles.deleteButton}
              onClick={() => handleDelete(notice.id)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

// Notification Component
const NotificationList = () => {
  const [notifications, setNotifications] = useState([
    { id: 1, text: "Notification 1" },
    { id: 2, text: "Notification 2" },
  ]);

  const handleDelete = (id) => {
    setNotifications(notifications.filter((notif) => notif.id !== id));
  };

  return (
    <div style={styles.listContainer}>
      <h2>Notifications</h2>
      {notifications.map((notif) => (
        <div key={notif.id} style={styles.listItem}>
          {notif.text}
          <div>
            <button style={styles.editButton}>Edit</button>
            <button
              style={styles.deleteButton}
              onClick={() => handleDelete(notif.id)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

const Dashboard = () => {
  const [selectedComponent, setSelectedComponent] = useState("notice");

  return (
    <div style={styles.container}>
      <SideBar onSelect={setSelectedComponent} />
      <div style={styles.mainContainer}>
        <Header />
        <div style={styles.content}>
          {selectedComponent === "notice" ? (
            <NoticeList />
          ) : (
            <NotificationList />
          )}
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    width: "100vw",
    height: "100vh",
    fontFamily: "'Poppins', sans-serif",
    backgroundColor: "#f4f4f4",
  },
  sidebar: {
    width: "250px",
    backgroundColor: "#121212",
    color: "white",
    display: "flex",
    flexDirection: "column",
    padding: "20px",
  },
  sidebarTitle: {
    fontSize: "24px",
    marginBottom: "30px",
  },
  menu: {
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  menuItem: {
    background: "white",
    color: "black",
    padding: "10px",
    textAlign: "center",
    borderRadius: "8px",
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "background 0.3s",
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: "#ddd",
    padding: "15px 20px",
    fontWeight: "bold",
  },
  logout: {
    color: "red",
    cursor: "pointer",
    transition: "color 0.3s",
  },
  mainContainer: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
  },
  content: {
    flex: 1,
    padding: "20px",
  },
  listContainer: {
    background: "white",
    padding: "20px",
    borderRadius: "8px",
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
  },
  listItem: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px",
    borderBottom: "1px solid #ddd",
  },
  editButton: {
    marginRight: "10px",
    padding: "5px 10px",
    background: "#007BFF",
    color: "white",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  deleteButton: {
    padding: "5px 10px",
    background: "red",
    color: "white",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
};

export default Dashboard;
