import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { logoutUser } from "../../redux/slices/userAuthSlice";

const SideBar = ({ onSelect }) => {
  return (
    <div style={styles.sidebar}>
      <h1 style={styles.sidebarTitle}>LOCALSE</h1>
      <div style={styles.menu}>
        <div style={styles.menuItem} onClick={() => onSelect("notice")}>
          Notice
        </div>
        <div style={styles.menuItem} onClick={() => onSelect("notification")}>
          Notification
        </div>
      </div>
    </div>
  );
};

const Header = () => {
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logoutUser());
  };

  return (
    <div style={styles.header}>
      <span>Employee Name</span>
      <span style={styles.logout} onClick={handleLogout}>
        Logout
      </span>
    </div>
  );
};

const Modal = ({ isOpen, onClose, type, onSubmit }) => {
  const [formData, setFormData] = useState({
    mobile: "",
    subject: "",
    content: "",
  });

  if (!isOpen) return null;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    onSubmit(formData);
    setFormData({ mobile: "", subject: "", content: "" });
    onClose();
  };

  return (
    <div style={styles.modalOverlay}>
      <div style={styles.modalContent}>
        <h2>{type === "notice" ? "Add Notice" : "Add Notification"}</h2>
        <input
          type="text"
          name="mobile"
          placeholder="Mobile Number"
          value={formData.mobile}
          onChange={handleChange}
          style={styles.input}
        />
        <input
          type="text"
          name="subject"
          placeholder="Subject"
          value={formData.subject}
          onChange={handleChange}
          style={styles.input}
        />
        <textarea
          name="content"
          placeholder="Content"
          value={formData.content}
          onChange={handleChange}
          style={styles.textarea}
        />
        <button onClick={handleSubmit} style={styles.submitButton}>
          Submit
        </button>
        <button onClick={onClose} style={styles.closeButton}>
          Close
        </button>
      </div>
    </div>
  );
};

const NoticeList = ({ onOpen }) => {
  return (
    <div style={styles.listContainer}>
      <div style={styles.listHeader}>
        <h2>Notices</h2>
        <button style={styles.addButton} onClick={() => onOpen("notice")}>
          + Add
        </button>
      </div>
      {/* Existing Notice Items */}
    </div>
  );
};

const NotificationList = ({ onOpen }) => {
  return (
    <div style={styles.listContainer}>
      <div style={styles.listHeader}>
        <h2>Notifications</h2>
        <button style={styles.addButton} onClick={() => onOpen("notification")}>
          + Add
        </button>
      </div>
      {/* Existing Notification Items */}
    </div>
  );
};

const Dashboard = () => {
  const [selectedComponent, setSelectedComponent] = useState("notice");
  const [modalOpen, setModalOpen] = useState(false);
  const [modalType, setModalType] = useState("notice");

  const openModal = (type) => {
    setModalType(type);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <div style={styles.container}>
      <SideBar onSelect={setSelectedComponent} />
      <div style={styles.mainContainer}>
        <Header />
        <div style={styles.content}>
          {selectedComponent === "notice" ? (
            <NoticeList onOpen={openModal} />
          ) : (
            <NotificationList onOpen={openModal} />
          )}
        </div>
      </div>
      <Modal
        isOpen={modalOpen}
        onClose={closeModal}
        type={modalType}
        onSubmit={() => {}}
      />
    </div>
  );
};

